  --popover-border: 210 4% 82%;

  --primary: 210 50% 45%;

  --primary-foreground: 210 50% 98%;

  --secondary: 0 0% 90%;

  --secondary-foreground: 210 6% 15%;

  --muted: 210 3% 94%;

  --muted-foreground: 210 4% 45%;

  --accent: 210 8% 92%;

  --accent-foreground: 210 8% 20%;

  --destructive: 0 84% 60%;

  --destructive-foreground: 0 84% 98%;

  --input: 210 4% 75%;
  --ring: 210 50% 45%;
  --chart-1: 210 50% 45%;
  --chart-2: 142 76% 36%;
  --chart-3: 45 93% 47%;
  --chart-4: 0 84% 60%;
  --chart-5: 217 91% 60%;

  --font-sans: Inter, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(210 20% 6% / 0.03);
  --shadow-xs: 0px 2px 0px 0px hsl(210 20% 6% / 0.05);
  --shadow-sm: 0px 2px 0px 0px hsl(210 20% 6% / 0.03), 0px 1px 2px -1px hsl(210 20% 6% / 0.07);
  --shadow: 0px 2px 0px 0px hsl(210 20% 6% / 0.05), 0px 1px 2px -1px hsl(210 20% 6% / 0.08);
